var ret = {};
ret.payload = {};

var solarPower = 0;

msg.payload.forEach(el => {

    if(el.type === "battery"){
        if(el._measurement === "soc") { ret.payload["openWB/set/houseBattery/%Soc"] = el._value }
        if (el._measurement === "ac_Power") { ret.payload["openWB/set/houseBattery/W"] = el._value*(-1000) }
    }

    if (el.type === "evu-meter" && el.instance === "1" && el.location === "basement") {
        if (el._measurement === "ac_Voltage_AN") { ret.payload["openWB/set/evu/VPhase1"] = el._value }
        if (el._measurement === "ac_Voltage_BN") { ret.payload["openWB/set/evu/VPhase2"] = el._value }
        if (el._measurement === "ac_Voltage_CN") { ret.payload["openWB/set/evu/VPhase3"] = el._value }
        if (el._measurement === "ac_Current_A") { ret.payload["openWB/set/evu/APhase1"] = el._value }
        if (el._measurement === "ac_Current_B") { ret.payload["openWB/set/evu/APhase2"] = el._value }
        if (el._measurement === "ac_Current_C") { ret.payload["openWB/set/evu/APhase3"] = el._value }
        if (el._measurement === "ac_Power_A") { ret.payload["openWB/evu/WPhase1"] = el._value*1000 }
        if (el._measurement === "ac_Power_B") { ret.payload["openWB/evu/WPhase2"] = el._value*1000 }
        if (el._measurement === "ac_Power_C") { ret.payload["openWB/evu/WPhase3"] = el._value*1000 }
        if (el._measurement === "ac_Freq") { ret.payload["openWB/set/evu/HzFrequenz"] = el._value }
        if (el._measurement === "ac_PF_A") { ret.payload["openWB/evu/PfPhase1"] = el._value }
        if (el._measurement === "ac_PF_B") { ret.payload["openWB/evu/PfPhase2"] = el._value }
        if (el._measurement === "ac_PF_C") { ret.payload["openWB/evu/PfPhase3"] = el._value }
        if (el._measurement === "ac_Power") { ret.payload["openWB/set/evu/W"] = el._value*(1000) }
    }

    if(el.type === "inverter"){
        if (el._measurement === "ac_Power") { solarPower = solarPower + (el._value * 1000) }
    }

})

ret.payload["openWB/set/pv/1/W"] = solarPower;

return [ret, msg];

